src/_scss/_components/_header.scss:
// _global.scss


body {
  .main-header {
    padding:12px 0;
    text-align: center;
    border-bottom: 1px solid $muted4;
    position:relative; 
    background-color: #FFF;
    margin-bottom:40px;

    &, > .container { 
      display:flex;
      align-items: center;
      @media (max-width:$mobileWidth) {
        justify-content: center;
      }
    } 
    > .container {
      &:before,
      &:after {display: none;}
    }


    .header-link {
      position:absolute;
      left:0px; 
      top: calc(100% + 18px);
      display: none;
      a {
        height:20px;
        font-size:11px;
        line-height: 115%;
        padding-left:0px;
        color:$muted2;
        display: flex;
        align-items: center;

        &:before {
          display:block;
          float:left;
          width:10px;
          height: 10px;

          -webkit-mask:url(data:image/svg+xml;base64,) no-repeat 50% 50%;
          mask:url(data:image/svg+xml;base64,) no-repeat 50% 50%;
          -webkit-mask-size: cover;
          mask-size: cover;
          background-color: $muted2;
          content:"";
        }

        &.back-cart-link {display:none;}
      }
      @media (max-width:$mobileWidth) {
        top:50%;
        transform: translateY(-50%);
        a {
          max-width: 80px;
          &:before {
            width: 17px;
            height: 17px;
          }
        }
      }
    } 

    .logo {
      max-width: calc(100% - 180px);

      svg.vtex-logo {
        height:50px;
      }
    }
    .logo,
    .logo img {
      display: inline-block;
      font-size:24px;
      border:none;
      line-height: 39px;

    }

    .checkout-steps {
      max-width: 540px;
      margin-left: auto;
      padding-right: 10px;
    }

  }

  &:not(.body-cart) {
    .main-header {
      @media (max-width: $mobileWidth) {
        padding:4px 0;
        margin-bottom: 10px;

        .v-custom-mhide {display: none !important;}

        &, > .container { 
          background:none;
          border:none;
          justify-content: center;
        }

        .logo {

          cursor: default;
          pointer-events: none;
          font-weight: bold;

          svg,
          img {display: none !important;}
          &:before {
            content:"checkout";
            text-transform: uppercase;
          }

          &, &:hover {
            color:$muted1;
            font-size: 14px;
          }
        }

        .header-link {display: block;}
        a.buy-more-link {display:none;}
        a.back-cart-link {
          display:block !important;
          font-size: 0;
          &:before {
            background: $muted1;
          }
        }
      }
    }
  }
}

