  	padding:0;
  	margin:0;
  	box-sizing: border-box;

  	background:#FFF;
		border-radius:$borderRadius;

		margin-bottom:20px !important;
		z-index: initial;

		border:$bordersContainers;


  	@if $countingSteps {
			padding:30px;
			@media(max-width: 768px) {
