		}

		&.client-pre-email {display:block !important;padding-bottom: calc( #{$inputHeight} + 14px + 30px);}

		.link-box-edit {
	    background: none;
	    box-shadow: none;
	    border: none;
	    color: $actionPrimary;
	    outline: none;

	    position: absolute;
	    right: 0;
	    top: 50%;
	    padding: 1px;
	    font-size: 14px;
	    transform: translateY(-50%);
	    opacity: 0.7;

	    transition: all ease 0.2s;


	    &:hover {
	    	opacity: 1;
	    }

	    i.icon-spinner {
		    font-size: 17px;
		    position: absolute;
		    right: 0;
		    display: none;
		    top: -7px;
			}

			&.active {
				i.icon-edit {display: none;}
				i.icon-spinner { display: block; }
			}
		}

		.accordion-toggle {
			padding:0;
    	@extend %steps-title;
    	> i {display: none;}
    	> span {
    		line-height: 20px;
    		display: block;
    		vertical-align: middle;
    		@if $countingSteps {
    			line-height: initial;
    		}
    	}
    }


    .accordion-toggle,
    .accordion-inner {
    	padding: 0;
    }


    input[type=text],
    input[type=tel],
    input[type=email],
    select  {
    	&:not(.pac-target-input) {
	    	@extend %input-style;
	    	width: 100%;
	    	max-width: 100%;
	    	outline: initial;
    	}
    }

    &.active {

	    @if $countingSteps {

	    }
    }

  }

	#payment-data {


		p.link.link-gift-card,
		.payment-group-item {
		  box-sizing: border-box;
		  margin: 0 6px 10px 0;
		  border-radius: $borderRadius;
		  border:1px solid $muted4;
		  opacity: 1;
		  text-decoration: none;
			color: $muted1;
			transition: all ease 0.3s;

			padding: 11px;
			text-align: center;
			display: flex;
			align-items: center;
			justify-content: center;
			background: $muted6;
			float: left;
			width:100%;

			&.active {
				background:#FFF;
				span {
					color:$emphasis;

				}
			}

			&.active,
			&:hover {
				box-shadow:0 0 6px $muted4;
				border:1px solid $actionPrimary;
			}
		}



		p.link.link-gift-card,
		fieldset.payment-group {
		  width: 100%;
		  left: 0;
		  display:block;
		  background: none;
		  position: relative;
		  margin:0;
		}

		#iframe-placeholder-creditCardPaymentGroup {
			padding-top: 32px;
			> .payment-group-item-cards {
				display: block;
				overflow: hidden;
				position: absolute;
