.orderform-template {
	display: flex !important;

	padding-top: 0px;
	min-height: 400px !important;
	align-items:flex-start;

	@media(max-width: 768px) {
		flex-wrap:wrap;
	}

	.orderform-template-holder{
		width: 100%;

		@media(min-width: 769px) {


			width: 61% !important;
			padding-right:20px;
			max-width: calc(100% - 271px);
			float:left;
			box-sizing: border-box;
		}

	}

	@media(max-width: $mobileWidth) {
  	width:100%;
  	padding-top:0px;
  }

	.cart-template.mini-cart {
		width: 39%;
  	min-width: 255px;

		position: -webkit-sticky;
		position: -moz-sticky;
		position: -o-sticky;
		position: -ms-sticky;
  	position: sticky;
    bottom: 0px;
		top: 20px;
		right:0;
		float: right;
		margin:0;

  	@media(max-width: 768px) {
  		width:100%;
  		position: relative;
  		top:0;
		}

		.cart-items {
			> .v-custom-indexed-item {display:none !important;}
		}

  	.cart-fixed {
  		height: auto !important;
  		position: relative !important;
	    max-width: 100%;
	    width: 100% !important;
			top:0;
			min-height: initial;

  		@media(min-width: 769px) {
  			&.affix {
  			}
  			&.affix-top {position: relative !important;}
  		}
  	}

		h2 {
			@extend %steps-title;
			text-align: left;
			border-top:none;
			background:none !important;
			margin-bottom: 15px;
		}


		.quantity {
			min-width: 10px;
			width: auto;
	    height: 10px;
	    display: inline-block;
	    background:$actionSecondary;
	    color:$actionPrimary;
		}

		.summary-template-holder {
			width:100%;
			background: none;
			border: none;
			padding: 0;

			.accordion-inner {margin-top: 0px;}

			.summary > .summary-coupon-wrap,
			.summary-coupon-wrap > .summary-coupon {
				display: block !important;
			}

			.summary-coupon p {
				margin:0;
			}

			.summary > .summary-coupon-wrap {
				border-top:1px solid $muted4;
				padding:8px 0;
			}

			input[type=text],
			button[type=submit],
			p.coupon-fields span.info > span {
				line-height: 30px !important;
				height:30px !important;
			}
		}

		.item {
	    padding: 10px 0;
	    margin: 0;
	    border: none;
	    border-bottom: 1px solid $muted5;
			&:last-child {border:none;}

			ul.service-list.unstyled {
				margin: 0;
				float: none;
				width: 100%;
				background: none;
				display: inline-block;
				clear: both;
			}
			.service-item,
			&.v-custom-indexed-item {
				background:$actionSecondary;
				border:1px dashed $muted4;
				border-radius:$borderRadius;

				width: 100%;
				display: flex;
				align-items: center;
				justify-content: space-between;

				box-sizing: border-box;
				padding: 5px 10px;
				margin:10px 0 0 0;

				.description {margin-top:0;}
				> a,
				.quantity,
				.shipping-date,
				.item-subscription {display:none;}

				.price {margin:0;}

				&:last-child {margin-bottom:0;}
			}

			&.v-custom-indexed-item {
				.item-unavailable {display:none;}
			}

	    .fn { width: calc(82% - 20px); }

	    .description {
			  display: flex;
			  justify-content: space-between;
			}

			.price,
			span.shipping-date {
			  white-space: nowrap;
			}

			.url {
				border-radius: $borderRadius;
    		overflow: hidden;
			}

		}

		.summary-totalizers {
	    margin-bottom: 0;
		}

		#go-to-cart-button {
	    position: absolute;
	    top:0px;
	    right: 7px;
	    color:$actionPrimary;
	    text-decoration: underline;
	    @media (max-width: $mobileWidth) {
	    	display: none;
	    }
		}



		.summary-totalizers {
			 tbody {
		    border-top:1px solid $muted4;
		    border-bottom:1px solid $muted4;
		    padding: 14px 0;
  			display: block;

  			tr {
			    &.CustomTax:not(.CustomTax--total) {
			      display:none !important;
			    }
			  }

		    td {
		      display: inline-block;
		      text-align: left !important;
		      width: auto;
		      color:#666;
		      font-size:13px;
		      padding: 0 0 3px 0 !important;
		      &.monetary {
		        text-align: right !important;
		        margin-left: auto;
		        float: right;
		      }
		    }

		    & + tbody {
			    border-top: none;
				}
				&.shipping-reset,
				&.shipping-calculate {display: none !important;}
		  }
		  tfoot {
		    tr td {
		      padding:14px 0 !important;
		      color:#000;
		      font-size:14px;
		      font-weight:bold;
		      text-align: left;
		      &.monetary {
		        text-align: right !important;
		        margin-left: auto;
		        float: right;
		        color:$actionPrimary;
		      }
		    }
		  }
		}

		.cart {
			margin-bottom: 0;
			max-height: calc(100vh - 355px);
			overflow:auto;

			&::-webkit-scrollbar {
				width: 6px
			}

			&::-webkit-scrollbar-track {
				background: transparent;
				border-radius: $borderRadius
			}

			&::-webkit-scrollbar-thumb {
				background: $muted4;
				border-radius: 30px
			}

			&:hover::-webkit-scrollbar-thumb {
				background: $muted5;
			}

			.cart-items:not(.v-loaded) {
				max-height: 100px;
				overflow: hidden;
				li.item {
					overflow:hidden;
					position:relative;
					border: none !important;
					> * {opacity:0;}

					&:after {
						content:"";
						width:100%;
						height:100%;
						display:block;
						top:0px;
						left:0;
						position:absolute;
						@extend %loading-shimmer;
					}
				}
			}
		}


	}

	.payment-confirmation-wrap {
		border:none;
		padding:0;

		.payment-submit-wrap {
			margin:10px 0 0 0;

			@media(max-width: 768px) {
				z-index: 9999;
				position: fixed;
				bottom: 0;
				width: 100%;
    		left: 0;
				padding: 0;
				background:#FFF;
				margin: 0 !important;
				button {
					border-radius:0 !important;
				}
			}
		}
		@media(max-width: 768px) {
			border:none !important;
			.payment-submit-wrap,
			button { margin:0 !important; }
		}

		#payment-data-submit {
			&:disabled span {
				display:none;
			}
		}
	}

	.summary-note {
		display:none !important;
		p.note-data {display: none !important;}
		> div {
			display: block !important;
		}
		.note-textarea {
			@extend %input-style;
			height:50px;
			line-height: 20px;
			max-width: 100%;
			width: 100%;
			font-family:$fontFamily;
			max-height: 100px;
		}
	}

	.custom-cart-template-wrap {
		position: relative;
		background: #FFF;
	  padding: 17px 20px;
	  text-align: left;
	  box-sizing: border-box;
	  border-radius:$borderRadius;

	  border: $bordersContainers;


	  .summary-cart-template-holder {
	    height: auto !important;
		}
	  .summary-template-holder {margin-top: 0;}
	}

}

// counting steps

	@if $countingSteps {

		.step.client-profile-data.active .box-step .form-step[style*="display: block"],
